CREATE TABLE `user` (`user_id` int(11) NOT NULL COMMENT '用户自增id,起始为10000',`user_account` varchar(11) NOT NULL COMMENT '用户账号（唯一标识）7-11位',`password` varchar(15) NOT NULL COMMENT '用户密码，7-15位',`user_email` varchar(30) NOT NULL COMMENT '用户邮箱',`user_name` varchar(10) NOT NULL COMMENT '用户名',`user_image` varchar(255) NOT NULL COMMENT '头像',`create_time` datetime NOT NULL COMMENT '创建日期(初始化就是创建时间)',`update_date` datetime NOT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '最后一次修改日期',`status` int(2) NOT NULL COMMENT '用户状态(1为正常)',`birthday` date NULL COMMENT '出生日期',`telephone` varchar(255) NULL COMMENT '用户电话',`role_id` int NULL,PRIMARY KEY (`user_id`) )ENGINE = InnoDBDEFAULT CHARACTER SET = utf8COLLATE = utf8_bin;CREATE TABLE `role` (`role_id` int NOT NULL COMMENT '唯一标识',`role` varchar(0) NOT NULL COMMENT '角色',PRIMARY KEY (`role_id`) );CREATE TABLE `article` (`article_id` int NOT NULL,`owner` int NULL,`ariticle_title` varchar(0) NOT NULL,`conten` text NOT NULL,`likes_num` int NULL,`scan_num` int NULL,`reply_id` int(255) NULL,PRIMARY KEY (`article_id`) );CREATE TABLE `replay` (`replay_id` int NOT NULL,`article_id` int NULL,`content` varchar(0) NOT NULL,`owner` int NULL,PRIMARY KEY (`replay_id`) );